mapper.update:
update TB_COUPON
set COUPON_DISCOUNT = #{couponDiscount,jdbcType=DECIMAL},
  COUPON_RULE = #{couponRule,jdbcType=VARCHAR},
  COUPON_START_TIME = #{couponStartTime,jdbcType=TIMESTAMP},
  COUPON_END_TIME = #{couponEndTime,jdbcType=TIMESTAMP}
where COUPON_ID = #{couponId,jdbcType=DECIMAL}
